mapper.select:
SELECT oo.*, opi.servcietel contact, pu.headportraitmax passengericonmax, pu.headportraitmin passengericonmin, ooc.cancelamount, ooc.cancelnature FROM `op_taxiorder` oo LEFT JOIN op_platforminfo opi ON opi.`status` = 1 LEFT JOIN pe_user pu ON pu.`status` = 1 AND pu.id = oo.userid LEFT JOIN op_taxiordercancel ooc ON ooc.`status` = 1 AND ooc.orderno = oo.orderno WHERE 1=1 AND oo.`status` = 1 AND oo.driverid = #{driverid}
AND oo.orderstatus IN (${taxistatuslist}) AND oo.paymentstatus IN (${taxipaystatuslist},'5')
AND ( oo.orderstatus IN (${taxistatuslist}) AND oo.paymentstatus IN (${taxipaystatuslist}) OR (oo.orderstatus = 8 AND ooc.cancelnature = 1 AND oo.paymentstatus = 0) )
AND oo.usetime >= #{starttime} AND oo.usetime < #{endtime}
AND oo.endtime >= #{starttime} AND oo.endtime < #{endtime}
ORDER BY oo.usetime ASC
ORDER BY oo.usetime DESC
LIMIT ${iDisplayStart},${iDisplayLength}

#获取出租车订单信息 SELECT 2 orderprop, pu.nickname username, pu.account userphone, 0 paymethod, opfi.servcietel contact, null organid, timestampdiff(second, oo.starttime, oo.endtime) times, pu.headportraitmin passengericonmin, pu.headportraitmax passengericonmax, pca.city city, oo.isusenow, oo.ordersource, oo.pricecopy, oo.undertime, oo.ordertime, oo.departuretime, oo.arrivaltime, oo.starttime, oo.endtime, oo.completetime, oo.canceltime, oo.mileage, oo.orderno, oo.ordertype type, oo.onaddress, oo.offaddress, oo.usetime usetime, oo.tripremark remark, oo.orderstatus `status`, oo.paymentstatus paystatus, oo.reviewstatus, oo.onaddrlng onlng, oo.onaddrlat onlat, oo.offaddrlng offlng, oo.offaddrlat offlat, oo.passengers passengers, oo.passengerphone passengerphone, oo.estimatedtime, oo.estimatedmileage, oo.estimatedcost, oo.orderamount,oo.paysurcharge surcharge, oo.fltno, oo.falltime, oo.userrate, oo.usercomment, oo.driverid, oo.vehicleid, oo.companyid, oo.oncity cityid, oo.ordersortcolumn, oo.usetype, oo.meterrange, oo.tripremark, oo.costtype, oo.startlng, oo.startllat, oo.endlng, oo.endllat, oo.meterrange, ooc.cancelamount,ooc.cancelnature FROM op_taxiorder oo LEFT JOIN pub_cityaddr pca ON pca.`status` = 1 AND oo.oncity = pca.id LEFT JOIN pe_user pu ON pu.`status` = 1 AND oo.userid = pu.id LEFT JOIN op_platforminfo opfi ON opfi.`status` = 1 LEFT JOIN op_taxiordercancel ooc ON ooc.`status` = 1 AND ooc.orderno = oo.orderno WHERE 1=1 AND oo.`status` = 1
AND oo.driverid = #{driverid}
AND oo.orderno = #{orderno}

SELECT
	COUNT(oo.orderno)
FROM
	`op_taxiorder` oo
	LEFT JOIN op_taxiordercancel ooc ON ooc.`status` = 1 AND ooc.orderno = oo.orderno
WHERE 1=1
AND oo.`status` = 1
AND oo.driverid = #{driverid}

AND oo.orderstatus IN (${taxistatuslist})
AND oo.paymentstatus IN (${taxipaystatuslist},'5')


AND (
	oo.orderstatus IN (${taxistatuslist}) AND oo.paymentstatus  IN (${taxipaystatuslist})
	OR (oo.orderstatus = 8 AND ooc.cancelnature = 1 AND oo.paymentstatus = 0)
)



		AND oo.usetime >= #{starttime}
		AND oo.usetime < #{endtime}


		AND oo.endtime >= #{starttime}
		AND oo.endtime < #{endtime}

SELECT oo.*, opi.servcietel contact, pu.headportraitmax passengericonmax, pu.headportraitmin passengericonmin, ooc.cancelamount,ooc.cancelnature FROM `op_taxiorder` oo LEFT JOIN op_platforminfo opi ON opi.`status` = 1 LEFT JOIN pe_user pu ON pu.`status` = 1 AND pu.id = oo.userid LEFT JOIN op_taxiordercancel ooc ON ooc.`status` = 1 AND ooc.orderno = oo.orderno WHERE 1=1 AND oo.`status` = 1
AND oo.driverid = #{driverid}
AND oo.orderno = #{orderno}

mapper.update:
BEGIN;
SELECT oo.* FROM `op_taxiorder` oo WHERE oo.orderno = #{orderno} FOR UPDATE;
UPDATE `op_taxiorder` oo SET 
	oo.paymentstatus = #{paymentstatus},
	oo.completetime=#{completetime},
	settlementtime=#{settlementtime},
	oo.ordersortcolumn = #{ordersortcolumn},
	oo.updatetime = NOW()
WHERE oo.orderno = #{orderno}
AND oo.paymentstatus IN(4,5,7);
COMMIT WORK;
